import './App.css';
import React, {useState, useEffect} from 'react';

function App() {
  const [factors, setFactors] = useState({a:41, b:26});
  const [alias, setAlias] = useState('');
  const [message, setMessage] = useState('');
  const [attempt, setAttempt] = useState('');


  const onChangeAttempt = (e) => {
    console.log("onChangeAttempt "+e.target.value);
    setAttempt(e.target.value);
  }


  const onChangeAlias = (e) => {
    console.log("onChangeAlias "+e.target.value);
    setAlias(e.target.value);
  }


  const handleSubmit = (e) => {
    if (/^[0-9]+$/.test(attempt)) {
      if (factors.a*factors.b===Number(attempt)){
        setMessage("Correct");
      } else {
        setMessage("Not Correct");
      }
    } else {
      setMessage("Your answer is not a valid integer.");
    }
  }


  const handleNext = (e) => {
    setMessage("Next Problem");
  }


    return (
      <div className="App">
       <h3>The problem is </h3>
       <h1>{factors.a} x {factors.b}</h1>


      <table>
        <tbody>
        <tr><td>
       <label htmlFor="attempt">Your answer</label>
       </td><td>
      <input type="text" name="attempt" value={attempt} onChange={onChangeAttempt} />
       </td></tr>
       <tr><td>
       <label htmlFor="alias">Your alias name</label>
       </td><td>
       <input type="text" name="alias" value={alias} onChange={onChangeAlias} />
       </td></tr>
       </tbody>
       </table>

       <br/>
       <button onClick={handleSubmit}>Submit</button>
       <br/>
       <button onClick={handleNext}>Next Problem</button>

       <h3>{message}</h3>
      </div>
    )
}
export default App;
